mapper.select:
select u.user_id, r.role_id, r.role_name from sys_user u left join sys_user_role ur on u.user_id = ur.user_id and u.user_id in
#{userId}
left join sys_role r on r.role_id = ur.role_id where u.del_flag = '0' and r.role_id is not null

select u.we_user_id, ifnull(u.data_scope,5) as data_scope, (SELECT GROUP_CONCAT(d.dept_name) FROM sys_user_manage_scop sums LEFT JOIN sys_dept d ON d.dept_id=sums.dept_id WHERE sums.user_id=u.user_id ) as scopeDept, u.user_id, u.user_name, u.position, u.phone_number, u.nick_name, u.open_userid, d.dept_id, d.dept_name, d.dept_en_name from sys_user u left join sys_user_dept ud on u.user_id = ud.user_id left join sys_dept d on ud.dept_id = d.dept_id left join sys_user_role sur on sur.user_id=u.user_id where u.is_user_leave=0
AND u.user_name like concat('%', #{sysUser.userName}, '%')
AND u.status = #{sysUser.status}
AND u.phone_number like concat('%', #{sysUser.phoneNumber}, '%')
AND date_format(u.create_time,'%y%m%d') >= date_format(#{sysUser.beginTime},'%y%m%d')
AND date_format(u.create_time,'%y%m%d') <= date_format(#{sysUser.endTime},'%y%m%d')
AND sur.role_id = #{sysUser.roleId}
AND FIND_IN_SET(#{sysUser.deptId},d.ancestors) or d.dept_id=#{sysUser.deptId}
AND ud.dept_id= #{sysUser.deptId}
AND u.del_flag = 0 GROUP BY u.we_user_id
limit ${pageDomain.pageSize * (pageDomain.pageNum-1)}, #{pageDomain.pageSize}

select count(DISTINCT u.we_user_id) from sys_user u left join sys_user_dept ud on u.we_user_id = ud.we_user_id left join sys_dept d on ud.dept_id = d.dept_id left join sys_user_role sur on sur.user_id=u.user_id where u.is_user_leave=0
AND u.user_name like concat('%', #{userName}, '%')
AND u.status = #{status}
AND u.phone_number like concat('%', #{phoneNumber}, '%')
AND date_format(u.create_time,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')
AND date_format(u.create_time,'%y%m%d') <= date_format(#{endTime},'%y%m%d')
AND sur.role_id = #{roleId}
AND FIND_IN_SET(#{deptId},d.ancestors) or d.dept_id=#{deptId}
AND ud.dept_id= #{deptId}
AND u.del_flag = 0

mapper.update:
update sys_user
we_user_id = #{weUserId}, dept_id = #{deptId}, user_name = #{userName}, position = #{position}, phone_number = #{phoneNumber}, sex = #{sex}, email = #{email}, biz_mail = #{bizMail}, leader = #{leader}, avatar = #{avatar}, thumb_avatar = #{thumbAvatar}, telephone = #{telephone}, nick_name = #{nickName}, ext_attr = #{extAttr}, we_user_status = #{weUserStatus}, qr_code = #{qrCode}, external_profile = #{externalProfile}, external_position = #{externalPosition}, address = #{address}, open_userid = #{openUserid}, password = #{password}, status = #{status}, create_by = #{createBy}, remark = #{remark}, user_type = #{userType}, update_time = sysdate()
where user_id = #{userId}

update sys_user
set status = #{status}
where user_id = #{userId}

update sys_user
set avatar = #{avatar}
where user_name = #{userName}

update sys_user
set password = #{password}
where user_name = #{userName}

insert into sys_user (user_id, dept_id, user_name, user_type, nick_name, email, phone_number, sex, avatar,password,status,login_ip,login_date,we_user_id,position, thumb_avatar, biz_mail, leader, telephone, ext_attr, we_user_status,qr_code, external_profile, external_position,address,open_userid,is_allocate,dimission_time,is_open_chat, create_by,create_by_id,create_time,update_by,update_by_id,update_time,remark) values
( #{weUser.userId},#{weUser.deptId},#{weUser.userName},#{weUser.userType},#{weUser.nickName},#{weUser.email},#{weUser.phoneNumber},#{weUser.sex}, #{weUser.avatar}, #{weUser.password}, #{weUser.status}, #{weUser.loginIp}, #{weUser.loginDate}, #{weUser.weUserId}, #{weUser.position}, #{weUser.thumbAvatar}, #{weUser.bizMail}, #{weUser.leader}, #{weUser.telephone}, #{weUser.extAttr}, #{weUser.weUserStatus}, #{weUser.qrCode}, #{weUser.externalProfile}, #{weUser.externalPosition}, #{weUser.address}, #{weUser.openUserid}, #{weUser.isAllocate}, #{weUser.dimissionTime}, #{weUser.isOpenChat}, #{weUser.createBy}, #{weUser.createById}, #{weUser.createTime}, #{weUser.updateBy}, #{weUser.updateById}, #{weUser.updateTime}, #{weUser.remark} )
ON DUPLICATE KEY UPDATE dept_id=IFNULL(VALUES(dept_id), sys_user.dept_id), user_name=IFNULL(VALUES(user_name), sys_user.user_name), user_type=IFNULL(VALUES(user_type), sys_user.user_type), nick_name=IFNULL(VALUES(nick_name), sys_user.nick_name), email=IFNULL(VALUES(email), sys_user.email), phone_number=IFNULL(VALUES(phone_number), sys_user.phone_number), sex=IFNULL(VALUES(sex), sys_user.sex), avatar=IFNULL(VALUES(avatar), sys_user.avatar), password=IFNULL(VALUES(password), sys_user.password), status=IFNULL(VALUES(status), sys_user.status), login_ip=IFNULL(VALUES(login_ip), sys_user.login_ip), login_date=IFNULL(VALUES(login_date), sys_user.login_date), we_user_id=IFNULL(VALUES(we_user_id), sys_user.we_user_id), position=IFNULL(VALUES(position), sys_user.position), thumb_avatar=IFNULL(VALUES(thumb_avatar), sys_user.thumb_avatar), biz_mail=IFNULL(VALUES(biz_mail), sys_user.biz_mail), leader=IFNULL(VALUES(leader), sys_user.leader), telephone=IFNULL(VALUES(telephone), sys_user.telephone), ext_attr=IFNULL(VALUES(ext_attr), sys_user.ext_attr), we_user_status=IFNULL(VALUES(we_user_status), sys_user.we_user_status), qr_code=IFNULL(VALUES(qr_code), sys_user.qr_code), external_profile=IFNULL(VALUES(external_profile), sys_user.external_profile), external_position=IFNULL(VALUES(external_position), sys_user.external_position), address=IFNULL(VALUES(address), sys_user.address), open_userid=IFNULL(VALUES(open_userid), sys_user.open_userid), is_allocate=IFNULL(VALUES(is_allocate), sys_user.is_allocate), dimission_time=IFNULL(VALUES(dimission_time), sys_user.dimission_time), is_open_chat=IFNULL(VALUES(is_open_chat), sys_user.is_open_chat), update_by=IFNULL(VALUES(update_by), sys_user.update_by), update_by_id=IFNULL(VALUES(update_by_id), sys_user.update_by_id), update_time=IFNULL(VALUES(update_time), sys_user.update_time), remark=IFNULL(VALUES(remark), sys_user.remark);

mapper.select:
SELECT su.*, (SELECT GROUP_CONCAT(d.dept_name) FROM sys_dept d WHERE sud.dept_id = d.dept_id) as dept_name FROM sys_user su LEFT JOIN sys_user_dept sud on su.user_id=sud.user_id WHERE su.del_flag = 0
AND (
OR su.we_user_id IN
#{item}
OR su.position IN
#{item}
OR sud.dept_id IN
#{item}
)

select su.user_id, su.dept_id, su.user_name, su.user_type, su.nick_name, su.sex, su.avatar, su.status, su.we_user_id, su.position, su.thumb_avatar, su.we_user_status, su.address, su.open_userid, su.is_allocate, su.dimission_time, su.is_open_chat, su.kf_status, sd.dept_id as sdept_id, sd.parent_id, sd.dept_name
from sys_user su
left join sys_user_dept sud on sud.user_id = su.user_id and sud.del_flag = 0 left join sys_dept sd on sd.dept_id = sud.dept_id and sd.del_flag = 0 left join sys_user_role sr on sr.user_id = su.user_id
and su.del_flag = 0
and su.user_id = #{userId}
and su.user_name like concat('%', #{userName}, '%')
and su.we_user_id = #{weUserId}
and su.we_user_id in
#{weUserId}
and sud.dept_id in
#{deptId}
and sr.role_id in
#{roleId}
and su.position in
#{position}
and su.we_user_status = #{weUserStatus}
and su.is_allocate = #{isAllocate}
and su.is_open_chat = #{isOpenChat}
and su.kf_status = #{kfStatus}
and su.sex = #{sex}
GROUP BY su.we_user_id
